#!/usr/bin/python# -*- coding: utf-8 -*-import pymysqlimport configparser# 读取配置config = configparser.ConfigParser()config.read(r"C:\Users\93284\PycharmProjects\Pyautomation\bin\automation.ini")IP = config.get("MySQL", "ip")MySQL_user = config.get("MySQL", "username")MySQL_password = config.get("MySQL", "password")MySQL_database = config.get("MySQL", "database")# MySQL配置conn = pymysql.connect(user=MySQL_user, passwd=MySQL_password, host=IP, db=MySQL_database)cur = conn.cursor()# 执行SQL语句ddl = open(r"MySQL.ddl").read()dml = open(r"MySQL.dml").read()sql = '''select * from ETL.etl_job;'''if __name__ == '__main__':    cur.execute(ddl)    cur.execute(dml)    res = cur.execute(sql)    print(cur.fetchall())