#!/usr/bin/python# -*- coding: utf-8 -*-import ETL.common as commonimport configparser# 建立Linux连接ssh = common.get_ssh()# 读取配置config = configparser.ConfigParser()config.read(r"C:\Users\93284\PycharmProjects\Pyautomation\ETL\automation.ini")etl_root = config.get(r'Automation', r'etl_root')# 创建一个列表存放所有需要创建的目录def make_dirs():    # 创建一个列表存放所有需要创建的目录    dir_list = set()    ssh.exec_command(r'rm -rf {0}/'.format(etl_root))    for path in config.options('Automation_path'):        dir_list.add(str(config.get("Automation_path", path)))    # 创建set中所有目录    for path in dir_list:        print(path)        ssh.exec_command(r'mkdir {0}'.format(path))if __name__ == '__main__':    make_dirs()